{
  "openapi": "3.0.0",
  "info": {
    "title": "arXiv API",
    "version": "1.0.0",
    "description": "API for accessing arXiv's repository of research papers and articles."
  },
  "servers": [
    {
      "url": "https://export.arxiv.org/api"
    }
  ],
  "paths": {
    "/query": {
      "get": {
        "operationId": "queryArxiv",
        "summary": "Search for papers in the arXiv repository.",
        "description": "Allows searching for papers based on a search query, with additional options for filtering and pagination.",
        "parameters": [
          {
            "in": "query",
            "name": "search_query",
            "schema": {
              "type": "string"
            },
            "required": false,
            "description": "The search query string. Used to search for papers based on titles, authors, abstracts, etc."
          },
          {
            "in": "query",
            "name": "id_list",
            "schema": {
              "type": "string"
            },
            "required": false,
            "description": "A comma-delimited list of arXiv paper IDs to retrieve."
          },
          {
            "in": "query",
            "name": "start",
            "schema": {
              "type": "integer",
              "default": 0
            },
            "required": false,
            "description": "The starting index for the search results, used for pagination."
          },
          {
            "in": "query",
            "name": "max_results",
            "schema": {
              "type": "integer",
              "default": 10
            },
            "required": false,
            "description": "The maximum number of results to return. Default is 10."
          },
          {
            "in": "query",
            "name": "sortBy",
            "schema": {
              "type": "string",
              "enum": [
                "relevance",
                "lastUpdatedDate",
                "submittedDate"
              ]
            },
            "required": false,
            "description": "Which field should be used to sort the results. Default is relevance."
          },
          {
            "in": "query",
            "name": "sortOrder",
            "schema": {
              "type": "string",
              "enum": [
                "ascending",
                "descending"
              ]
            },
            "required": false,
            "description": "The sort order. Default is descending."
          }
        ],
        "responses": {
          "200": {
            "description": "A successful response containing an Atom feed of papers.",
            "content": {
              "application/atom+xml": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "title": {
                      "type": "string"
                    },
                    "id": {
                      "type": "string"
                    },
                    "updated": {
                      "type": "string",
                      "format": "date-time"
                    },
                    "author": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "name": {
                            "type": "string"
                          }
                        }
                      }
                    },
                    "entry": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/Entry"
                      }
                    }
                  }
                }
              }
            }
          },
          "400": {
            "description": "Bad request, possibly due to invalid query parameters."
          },
          "500": {
            "description": "Internal server error."
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Entry": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string"
          },
          "id": {
            "type": "string"
          },
          "published": {
            "type": "string",
            "format": "date-time"
          },
          "updated": {
            "type": "string",
            "format": "date-time"
          },
          "summary": {
            "type": "string"
          },
          "author": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "name": {
                  "type": "string"
                }
              }
            }
          },
          "link": {
            "type": "object",
            "properties": {
              "href": {
                "type": "string"
              },
              "rel": {
                "type": "string"
              },
              "type": {
                "type": "string"
              }
            }
          }
        }
      }
    }
  }
}
